import './App.css';
import Axios from 'axios'
import React, { useRef, useState } from 'react';
import axios from 'axios';
import fileDownload from 'js-file-download'

function App() {

  const [name, setName] = useState("");
  const [pathH, setPath] = useState("");

  const [employeesList, setEmployeeList] = useState([]);



  const getEmployees = () => {
    Axios.get('http://localhost:3001/employees').then((response) => {
      setEmployeeList(response.data)
    });
  };

  const [file, setFile] = useState(''); 
  const [data, getFile] = useState({ name: "", path: "" });
  const el = useRef(); 

  const handleChange = (e) => {
    const file = e.target.files[0]; 
    console.log(file);
    setFile(file); 
}

  const uploadFile = () => {
      const formData = new FormData();
      formData.append('file', file); // appending file
      axios.post('http://localhost:3001/create', formData).then(res => {
          console.log(res);
          getFile({ name: res.data.name,
                  path: 'http://localhost:3001' + res.data.path
                })
      }).catch(err => console.log(err))}



  return (
    <div className="App">
      <div className="information">
      <label>Name</label>
      <input type="text" onChange={(event) => {
        setName(event.target.value);
        }}/>
      <label>Path</label>
      <input type="text" onChange={(event) => {
        setPath(event.target.value);
        }}/>

      <button onClick={uploadFile}>Add File</button>
      </div>

      <div className="employees">
      <button onClick={getEmployees}>Show Folder</button>

      {employeesList.map((val,key) => {
        return (
        <div className="employee"> 
        <div>
          <h3>Name: {val.name}</h3> 
          <h3>Path: {val.pathH}</h3> 
        </div>
        </div>
        )
      })}
      </div>
    </div>
  );
}

export default App;
