import React, { useRef, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { saveAs } from 'file-saver'

function FileUpload() {

    const [file, setFile] = useState(''); 
    const [data, getFile] = useState({ name: "", path: "" });
    const el = useRef(); 
    const [employeesList, setEmployeeList] = useState([]);


    const handleChange = (e) => {
        const file = e.target.files[0]; 
        console.log(file);
        setFile(file); 
    }

    const uploadFile = () => {
        const formData = new FormData();
        formData.append('file', file); // appending file
        axios.post('http://localhost:3001/upload', formData).then(res => {
            console.log(res);
            getFile({ name: res.data.name,
                     path: 'http://localhost:3001' + res.data.path
                   })
        }).catch(err => console.log(err))}

    const getFolder = () => {
      axios.get('http://localhost:3001/show').then((response) => {
        setEmployeeList(response.data)
      });
    };

    const deleteFile = (id) => {
      axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
        setEmployeeList(employeesList.filter((val) => {
          return val.id !== id
        }))
      });
    };

    const downloadFile = (id) => {
        setEmployeeList(employeesList.filter((val) => {
          const downloadFileid = val.id !== id
          return console.log(downloadFileid)
        }))
    };



    return (
      <div className="App">
        <input type="file" ref={el} onChange={handleChange} />
        <button onClick={uploadFile}>Upload</button>
        <hr />
        {data.path && <img src={data.path} alt={data.name} />}

        <div className="employees">
        <button onClick={getFolder}>ShowFolder</button>

        <table className="table table-hover table-striped">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Path</th>
            <th>Action</th>
          </tr>
        </thead>

        <tbody>
        {employeesList.map((val,key) => (
            <tr key={val.id}>
              <td>{val.id}</td>
              <td>{val.name}</td> 
              <td>{val.pathH}</td>
              <button onClick={() => {deleteFile(val.id)}}>delete</button>
              <button onClick={() => {downloadFile(val.id)}}>download</button>
            </tr>

        ))}

        </tbody> 
        </table> 
        </div>
      </div>
    );     
}

export default FileUpload;
